import React from 'react'
import { GET_ANIMALS } from '../graphql/queries'
import CardList from './cardList'
import { useQuery } from '@apollo/client';

const AnimalList = () => {

    const { loading, error, data } = useQuery(GET_ANIMALS);

    if (loading) return 'Loading...';
    else if (error) return `Error! ${error.message}`;
    // <CardList id={item.id} name={"name"} food={"food"} type={"type"} characterstic={"charaat"} legs={"legs"} />

    return (
        <div style={{ display: 'grid', gridTemplateColumns: '1fr', gridGap: '10px' }}>
            {
                data.Animals.map(item => {
                    return <CardList id={item.id}  food={"food"} type={"type"} characterstic={"charaat"} legs={"legs"} />
                })
            }
        </div>
    )
}

export default AnimalList
