import Paragraph from 'antd/lib/skeleton/Paragraph'
import React, { useState } from 'react'

const CardList = ({ id, name, food, type, characterstic, legs }) => {

    const styles = getStyles();

    const Paragraph = ({ value }) => {
        return <span style={styles.textBold} >{value}</span>
    }

    const Title = ({ value }) => {
        return <span style={styles.colorGrey} >{value}</span>
    }

    return (
        <div style={{display:'flex', flexDirection:'row', justifyContent:'space-between', width:'100%', alignContent:'space-between', background:'red', flexWrap:'wrap', flexGrow:'1'}}>
            <Title value={"id"} /> <Paragraph value={id} />
            <Title value={"name"} /> <Paragraph value={name} />
            <Title value={"id"} /> <Paragraph value={id} />
            <Title value={"id"} /> <Paragraph value={id} />
            <Title value={"id"} /> <Paragraph value={id} />
        </div>
    )
}

const getStyles = () => ({
    textBold: {
        fontWeight: "bold",
    },
    colorGrey: {
        color: 'grey'
    }
})


export default CardList
