import React, { useEffect, useState } from 'react'
import { GET_ANIMALS } from '../graphql/queries'
import CardList from './cardList'
import { useQuery } from '@apollo/client';
import Header from './header'

const AnimalList = () => {

    const [edited, setEdited] = useState(1)
    useEffect(() => {
        setEdited(2)
    }, [edited])

    function handle(value) {
        console.log('im clicked')
        setEdited(5)
    }

    const { loading, error, data } = useQuery(GET_ANIMALS)
    if (loading) return 'Loading...'
    else if (error) return `Error! ${error.message}`

    return (
        <div>
            <Header callback={handle} />
            {
                edited > 0 && <div style={{ display: 'grid', gridTemplateColumns: '1fr', gridGap: '10px' }}>
                    {
                        data.Animals.map(item => {
                            return <CardList id={item.id} name={item.name} food={item.food} type={item.type} characterstic={item.properties.type} legs={item.properties.legs} />
                        })
                    }
                </div>
            }
        </div>
    )
}

export default AnimalList
